    private void btnBrowseActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBrowseActionPerformed
        int returnVal = filePicker.showOpenDialog(this);
        if (returnVal == JFileChooser.APPROVE_OPTION) {
            File file = filePicker.getSelectedFile();
            // What to do with the file, e.g. display it in a TextArea
            txtDirectory.setText(file.getAbsolutePath());
        } else {
            System.out.println("Eleccion de archivo cancelada.");
        }
    }//GEN-LAST:event_btnBrowseActionPerformed

    private void btnEncryptActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEncryptActionPerformed
        int r,g,b;
        r = Integer.parseInt(txtR.getText());
        g = Integer.parseInt(txtG.getText());
        b = Integer.parseInt(txtB.getText());
        ImageHelper i = new ImageHelper(txtDirectory.getText(),r,g,b);
        
        if(i.generateModifiedBmp()){
            JOptionPane.showMessageDialog(this, "Archivo modificado en 'corazon_c.bmp'.", "Exito", JOptionPane.INFORMATION_MESSAGE);
        }else{
            JOptionPane.showMessageDialog(this, "Error al generar archivo modificado.", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnEncryptActionPerformed

    private void btnDecryptActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDecryptActionPerformed
        int r,g,b;
        r = Integer.parseInt(txtR.getText());
        g = Integer.parseInt(txtG.getText());
        b = Integer.parseInt(txtB.getText());
        ImageHelper i = new ImageHelper(txtDirectory.getText(),r,g,b);
        
        if(i.generateOriginalBmp()){
            JOptionPane.showMessageDialog(this, "Archivo modificado en 'corazon_d.bmp'.", "Exito", JOptionPane.INFORMATION_MESSAGE);
        }else{
            JOptionPane.showMessageDialog(this, "Error al generar archivo modificado.", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnDecryptActionPerformed

